import './App.css';
import { useState } from 'react';

function App() {
  const [inputValue, setInputValue] = useState({ rgb1: '#FFFFFF', rgb2: '#FFFFFF' });
  const [tempValue, setTempValue] = useState({ rgb1: '#FFFFFF', rgb2: '#FFFFFF' });

  const handleChangeFirst = (event) => {
    setTempValue(prev => ({ ...prev, rgb1: event.target.value }));
  }

  const handleChangeLast = (event) => {
    setTempValue(prev => ({ ...prev, rgb2: event.target.value }));
  }

  const handleSubmit = (event) => {
    event.preventDefault();
    setInputValue(tempValue);
  }

  return (
    <>
      <div className="buttonsRgb">
        <form onSubmit={handleSubmit}>
          <div className='inputs'>
            <input className="firstColor" placeholder='RGB' onChange={handleChangeFirst} />
            <input className="lastColor" placeholder='RGB' onChange={handleChangeLast} />
          </div>
          <button type="submit" className="goGradient">PAINT</button>
        </form>
      </div>
      <div className="gradient" style={{ background: `linear-gradient(${inputValue.rgb1}, ${inputValue.rgb2})` }}>
        {/* Здесь можно добавить текст или другие элементы для отображения градиента */}
      </div>
    </>
  );
}

export default App;
